import { Component, Inject } from '@angular/core';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-edit-category-dialog',
  templateUrl: './edit-category-dialog.component.html',
  styleUrls: ['./edit-category-dialog.component.css']
})
export class EditCategoryDialogComponent {
  constructor(
    public dialogRef: MatDialogRef<EditCategoryDialogComponent>,
    @Inject(MAT_DIALOG_DATA) public data: { categoryId: number, categoryName: string }
  ) {}

  onSaveClick(): void {
    // Здесь вы можете добавить логику сохранения изменений
    this.dialogRef.close({ categoryId: this.data.categoryId, categoryName: this.data.categoryName });
  }

  onCancelClick(): void {
    this.dialogRef.close();
  }

}
<h1 mat-dialog-title>Edit Category</h1>
<div mat-dialog-content>
  <p>Category Name: {{ data.categoryName }}</p>
  <mat-form-field>
    <input matInput [(ngModel)]="data.categoryName" placeholder="Category Name">
  </mat-form-field>
</div>
<div mat-dialog-actions>
  <button mat-button (click)="onCancelClick()">Cancel</button>
  <button mat-button color="primary" (click)="onSaveClick()">Save</button>
</div>
