import { Component, OnInit } from '@angular/core';
import { ContactDataService, Contact } from './contactdata.service';
import { MatDialog } from '@angular/material/dialog';
import { AddContactDialogComponent } from '../add-contact-dialog/add-contact-dialog.component';
import { EditContactDialogComponent } from '../edit-contact-dialog/edit-contact-dialog.component';


@Component({
  selector: 'contacts',
  templateUrl: './contacts.component.html',
  styleUrls: ['./contacts.component.css']
})
export class ContactsComponent implements OnInit {
  public contacts: Contact[] | undefined;
  public showAddForm = false; // Переменная для отображения/скрытия формы
  public newContact: Contact = {
      firstName: '', lastName: '', categoryId: 0, phoneId: 0,
      contactId: 0
  };


  constructor(private contactDataSrv: ContactDataService, private dialog: MatDialog) {
    }
    
  ngOnInit() {
    this.contacts = this.contactDataSrv.contacts;
    this.showAll();
    }
    /*
  showAll() {
    this.contactDataSrv.getContactsWithDetails().subscribe(data => {
      this.contacts = data;
    });
  }  */

  showAll() {
    this.contactDataSrv.showAll();
  }
  /*
  addContact(): void {
    const dialogRef = this.dialog.open(AddContactDialogComponent, {
      width: '400px', // Установите нужные размеры окна
      data: {} // Передайте данные, если необходимо
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result) {
      // Получите результат после закрытия окна
        console.log('Данные из диалога:', result);
        this.contactDataSrv.addContact(result);
    } else {
      console.log('Диалог закрыт без данных');
    }
    });
  }  */

  addContact(): void {
    const dialogRef = this.dialog.open(AddContactDialogComponent, {
      width: '400px',
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        this.contactDataSrv.addContact(result).subscribe(
          response => {
            console.log('Данные успешно добавлены:', response);
          },
          error => {
            console.error('Не удалось добавить данные:', error);
          },
          () => {
            console.log('Диалог успешно закрыт!');
          }
        );
      } else {
        console.log('Диалог закрыт без данных');
      }
    });
  }

  updateContact(contactId: number | undefined, firstName: string | undefined, lastName: string | undefined, categoryId: number | undefined, phoneId: number | undefined): void {
    if (contactId === undefined || categoryId === undefined || phoneId === undefined) {
      return;
    }
    const dialogRef = this.dialog.open(EditContactDialogComponent, {
      data: { editedContactId: contactId },
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result === 'saved') {
        this.contactDataSrv.updateContact(contactId, firstName, lastName, categoryId, phoneId);
      }
    });
  }


  deleteContact(contactId: number): void {

    this.contactDataSrv.deleteContact(contactId);
  }
}

<h1>Contacts</h1>

<p *ngIf="!contacts"><em>Please, choose action.</em></p>
<div class="example-button-row">
  <button mat-button (click)="addContact()" class="btn btn-accent">Add Contact</button>
</div>
<table class='table table-striped' *ngIf="contacts">
  <thead>
    <tr>
      <!--<th>Id</th>-->
      <th>First Name</th>
      <th>Last Name</th>
      <th>Category</th>
      <th>Phone</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let contact of contacts">
      <!--<td>{{ contact.contactId }}</td>-->
      <td>{{ contact.firstName }}</td>
      <td>{{ contact.lastName }}</td>
      <td>{{ contact.categoryId }}</td>
      <td>{{ contact.phoneId }}</td>
      <td>
        <button mat-button (click)="updateContact(contact.contactId, contact.firstName, contact.lastName, contact.categoryId, contact.phoneId)" class="btn btn-accent">
          Edit
        </button>

        <button mat-button *ngIf="contact.contactId !== undefined"
                (click)="deleteContact(contact.contactId)" class="btn Warn">
          Delete
        </button>
      </td>
    </tr>
  </tbody>
</table>
