import { Injectable, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';

export class Contact {
  contactId: number | undefined;
  firstName: string | undefined;
  lastName: string | undefined;
  categoryId: number | undefined;
  phoneId: number | undefined;
}

@Injectable()
export class ContactDataService {

  public contacts: Contact[] = [];
    baseUrl: string;

    constructor(private http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
        this.http = http;
        this.baseUrl = baseUrl;
    }

    showAll() {
      this.http.get<Contact[]>(`https://localhost:7217/api/Contacts`).subscribe(result => {
       // this.contacts = result;
        this.contacts.length = 0;
        this.contacts.push(...result as Contact[]);
        console.log(result);
      }, error => console.error(error));
    }
   
  addContact() {

    const body = {
      //contact_id: 12,
      firstName: "Fedor",
      lastName: "Fedorov",
      categoryId: 3,
      phoneId: 17
          
    }

    this.http.post(`https://localhost:7217/api/Contacts`, body).subscribe(result => {
            this.showAll();
        }, error => console.error(error));
    }

  updateContact() {

        const body = {
          //contact_id: 12,
          firstName: "Fedor",
          lastName: "Fedorov",
          category_id: 3,
          phone_id: 17
        }

    this.http.put(`https://localhost:7217/api/Contacts/12`, body).subscribe(result => {
            this.showAll();
        }, error => console.error(error));
    }

  deleteContact() {

    this.http.delete(`https://localhost:7217/api/Contacts/12`).subscribe(result => {
            this.showAll();
        }, error => console.error(error));
    }
}
