import { Component, OnInit } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { CategoryDataService, Category } from './categorydata.service';
import { HttpClient } from '@angular/common/http';
import { AddCategoryDialogComponent } from './addNewCategory.component';


//import { DataSource } from '@angular/cdk/collections';
//import { Observable, ReplaySubject } from 'rxjs';
//import { MatTableModule } from '@angular/material/table';
//import { MatButtonModule } from '@angular/material/button';
//import { MatTooltipModule } from '@angular/material/tooltip';
//import { MatIconModule } from '@angular/material/icon';


@Component({
  selector: 'categories', 
  templateUrl: './categories.component.html',
  styleUrls: ['./categories.component.css'],
})
 /* @Component({
    selector: 'button-types-example',
    templateUrl: 'button-types-example.html',
    styleUrls: ['button-types-example.css'],
    standalone: true,
    imports: [MatButtonModule, MatTooltipModule, MatIconModule],
  }) */

export class CategoriesComponent implements OnInit {
  public categories: Category[] | undefined;

  constructor(private categoryDataSrv: CategoryDataService, private dialog: MatDialog) {
    }
    
  ngOnInit() {
    this.showAll();
      /*this.categories = this.categoryDataSrv.categories;*/
    }
    
    showAll() {
      this.categoryDataSrv.showAll();
      this.categories = this.categoryDataSrv.categories;
    }

  addCategory() {
    // Открываем диалоговое окно для добавления контакта
    const dialogRef = this.dialog.open(AddCategoryDialogComponent, {
      width: '400px',
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        console.log('Данные из диалога:', result);
        this.categoryDataSrv.addCategory(result);
        // Дополнительные действия
      } else {
        console.log('Диалог закрыт без данных');
      }
    });
  }
  /*
  addCategory() {

      this.categoryDataSrv.addCategory();
    } 
   */ 
  updateCategory() {

      this.categoryDataSrv.updateCategory();
    }

  deleteCategory() {
      this.categoryDataSrv.deleteCategory();
    }
}
<h1>Categories</h1>

<!--<p>This component demonstrates fetching data from the server.</p>-->

<p *ngIf="!categories"><em>Please, choose action.</em></p>

<table class='table' *ngIf="categories">
  <thead>
    <tr>
      <th>Id</th>
      <th>Category Name</th>
      <button mat-button (click)="updateCategory()" class="btn btn-accent">Update Category</button>
      <button mat-button (click)="deleteCategory()" class="btn Warn">Delete Category</button>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let category of categories">
      <td>{{ category.categoryId }}</td>
      <td>{{ category.categoryName }}</td>
    </tr>
  </tbody>
</table>


<div class="example-button-row">
  <button mat-button (click)="showAll()" class="btn btn-accent">Show all</button>
  <button mat-button (click)="addCategory()" class="btn btn-accent">Add Category</button>
  <!--<button mat-button (click)="updateCategory()" class="btn btn-accent">Update Category</button>
  <button mat-button (click)="deleteCategory()" class="btn Warn">Delete Category</button>-->
</div>
