import { Component, Inject } from '@angular/core';
import { MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Contact } from '../contacts/contactdata.service';

@Component({
  selector: 'app-edit-contact-dialog',
  templateUrl: './edit-contact-dialog.component.html',
  styleUrls: ['./edit-contact-dialog.component.css']
})
export class EditContactDialogComponent {
  public contactForm: FormGroup;
  public editedContact: Contact | undefined;

  constructor(
    private dialogRef: MatDialogRef<EditContactDialogComponent>,
    private formBuilder: FormBuilder,
    @Inject(MAT_DIALOG_DATA) public data: { editedContact: Contact }
  ) {
    this.contactForm = this.formBuilder.group({
      firstName: ['', Validators.required],
      lastName: ['', Validators.required],
      phoneId: ['', Validators.required],
      categoryId: ['', Validators.required]
    });

    // Убедимся, что данные переданы и установим значения формы
    this.editedContact = data.editedContact || undefined;
    if (this.editedContact) {
      this.contactForm.patchValue(this.editedContact);
    }
  }

  onSubmit() {
    if (this.contactForm.valid) {
      this.dialogRef.close(this.contactForm.value);
    }
  }

  onCancel() {
    this.dialogRef.close();
  }
}
<h2 mat-dialog-title>{{ editedContact ? 'Редактирование контакта' : 'Добавление контакта' }}</h2>
<div mat-dialog-content>
  <form [formGroup]="contactForm" (ngSubmit)="onSubmit()">
    <mat-form-field>
      <input matInput placeholder="Имя" formControlName="firstName" required>
      <mat-error *ngIf="contactForm.get('firstName')?.hasError('required')">Имя обязательно</mat-error>
    </mat-form-field>

    <mat-form-field>
      <input matInput placeholder="Фамилия" formControlName="lastName" required>
      <mat-error *ngIf="contactForm.get('lastName')?.hasError('required')">Фамилия обязательна</mat-error>
    </mat-form-field>

    <mat-form-field>
      <input matInput placeholder="Номер телефона" formControlName="phoneId" required>
      <mat-error *ngIf="contactForm.get('phoneId')?.hasError('required')">Номер телефона обязателен</mat-error>
    </mat-form-field>

    <mat-form-field>
      <input matInput placeholder="Категория" formControlName="categoryId" required>
      <mat-error *ngIf="contactForm.get('categoryId')?.hasError('required')">Категория обязательна</mat-error>
    </mat-form-field>
  </form>
</div>
<div mat-dialog-actions>
  <button class="btn btn-primary" (click)="onCancel()">Отмена</button>
  <button class="btn btn-primary"
          (click)="updateContact(contactForm.value.contactId, contactForm.value.firstName, contactForm.value.lastName, contactForm.value.categoryId, contactForm.value.phoneId)"
          [disabled]="!contactForm.valid">

    {{ editedContact ? 'Сохранить изменения' : 'Добавить контакт' }}
  </button>
</div>
