import { Injectable, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';

export class PhoneNumber {
  phoneId: number | undefined;
  numberPhone: string | undefined;
}

@Injectable()
export class PhoneNumberDataService {

  public phoneNumbers: PhoneNumber[] = [];
    baseUrl: string;

    constructor(private http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
        this.http = http;
        this.baseUrl = baseUrl;
    }

    showAll() {
      this.http.get<PhoneNumber[]>(`https://localhost:7217/api/PhoneNumbers`).subscribe(result => {
        //this.phoneNumbers = result;
        this.phoneNumbers.length = 0;
        this.phoneNumbers.push(...result as PhoneNumber[]);
        console.log(result);
      }, error => console.error(error));
    }
    /*
    showFromCA() {
        this.http.get(this.baseUrl + 'api/Authors/state/CA').subscribe(result => {
          this.categories.length = 0;
          this.categories.push(...result as Category[]);
        }, error => console.error(error));
    } */

  addPhoneNumber() {

        const body = {
          phone_id: 5,
          phone_number: "89078887755"
        }

    this.http.post(`https://localhost:7217/api/PhoneNumbers`, body).subscribe(result => {
            this.showAll();
        }, error => console.error(error));
    }

  updatePhoneNumber() {

        const body = {
          phone_id: 5,
          phone_number: "89078887755"
        }

    this.http.put(`https://localhost:7217/api/PhoneNumbers/17`, body).subscribe(result => {
            this.showAll();
        }, error => console.error(error));
    }

  deletePhoneNumber() {

    this.http.delete(`https://localhost:7217/api/PhoneNumbers/17`).subscribe(result => {
            this.showAll();
        }, error => console.error(error));
    }
}
