import { Injectable, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';

export class Category {
  categoryId: number | undefined;
  categoryName: string | undefined;
}

@Injectable()
export class CategoryDataService {

  public categories: Category[] = [];
    baseUrl: string;

    constructor(private http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
        this.http = http;
        this.baseUrl = baseUrl;
    }

    showAll() {
      this.http.get<Category[]>(`https://localhost:7217/api/Categories`).subscribe(result => {
        //this.categories = result;
        this.categories.length = 0;
        this.categories.push(...result as Category[]);
        console.log(result);
      }, error => console.error(error));
    }
    /*
    showFromCA() {
        this.http.get(this.baseUrl + 'api/Authors/state/CA').subscribe(result => {
          this.categories.length = 0;
          this.categories.push(...result as Category[]);
        }, error => console.error(error));
    } 
*/
  addCategory(newCategory: { categoryName: string }): void {
    console.log('Тело запроса:', newCategory);

    const body = {
      category_name: newCategory
    };

    /*const headers = { 'Content-Type': 'application/json' };*/

    this.http.post(`https://localhost:7217/api/Categories`, body/*, { headers }*/)
      .subscribe(
        result => {
          this.showAll();
        },
        error => {
          console.error('Ошибка при добавлении категории:', error);
        }
      );
  }

  updateCategory() {

        const body = {
          category_id: 5,
          category_name: "Job"
        }

    this.http.put(`https://localhost:7217/api/Categories/5`, body).subscribe(result => {
            this.showAll();
        }, error => console.error(error));
    }

  deleteCategory() {

    this.http.delete(`https://localhost:7217/api/Categories/5`).subscribe(result => {
            this.showAll();
        }, error => console.error(error));
    }
}
