import { Component, Inject } from '@angular/core';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';


@Component({
  selector: 'app-add-contact-dialog',
  templateUrl: './add-contact-dialog.component.html',
  styleUrls: ['./add-contact-dialog.component.css']
})
export class AddContactDialogComponent {
  constructor(
    public dialogRef: MatDialogRef<AddContactDialogComponent>,
    @Inject(MAT_DIALOG_DATA) public data: any
  ) { }

  onNoClick(): void {
    this.dialogRef.close();
  }
}


export class AddPhoneNumberDialogComponent {
  phoneNumberForm: FormGroup;

  constructor(public dialogRef: MatDialogRef<AddPhoneNumberDialogComponent>, private fb: FormBuilder) {
    this.phoneNumberForm = this.fb.group({
      numberPhone: ['', Validators.required]
    });
  }

  onCancel(): void {
    this.dialogRef.close();
  }

  onSubmit(): void {
    console.log('onSubmit() called');
    if (this.phoneNumberForm.valid) {
      console.log('Form is valid');

      const numberPhone = this.phoneNumberForm.value.numberPhone;
      console.log('Добавлена новая категория:', numberPhone);

      // Закрываем диалоговое окно и передаем данные в родительский компонент
      this.dialogRef.close(numberPhone);
      console.log('Dialog closed successfully');
    } else {
      console.log('Form is invalid');
    }
  }
<div *ngIf="showAddForm">
  <h3>Add New Contact</h3>
  <form (ngSubmit)="addNewContact()">
    <div class="form-group">
      <label for="firstName">First Name:</label>
      <input type="text" id="firstName" [(ngModel)]="newContact.firstName" name="firstName" required>
    </div>
    <div class="form-group">
      <label for="lastName">Last Name:</label>
      <input type="text" id="lastName" [(ngModel)]="newContact.lastName" name="lastName" required>
    </div>
    <div class="form-group">
      <label for="category">Category:</label>
      <select id="category" [(ngModel)]="newContact.categoryId" name="category" required>
        <!-- Здесь нужно добавить опции для выбора категории -->
      </select>
    </div>
    <div class="form-group">
      <label for="phone">Phone:</label>
      <select id="phone" [(ngModel)]="newContact.phoneId" name="phone" required>
        <!-- Здесь нужно добавить опции для выбора телефона -->
      </select>
    </div>
    <button type="submit" class="btn btn-accent">Add Contact</button>
  </form>
</div>


<h2 mat-dialog-title>Добавление номера телефона</h2>
<div mat-dialog-content>
  <form [formGroup]="phoneNumberForm" (ngSubmit)="onSubmit()">
    <mat-form-field>
      <input matInput placeholder="Номер телефона" formControlName="numberPhone" required>
    </mat-form-field>
  </form>
</div>
<div mat-dialog-actions>
  <button class="btn btn-primary" (click)="onCancel()">Отмена</button>
  <button class="btn btn-primary" type="button" (click)="onSubmit()"
          [disabled]="!phoneNumberForm.valid">
    Добавить
  </button>
</div>
