import { Component, Inject } from '@angular/core';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';



@Component({
  selector: 'app-add-contact-dialog',
  templateUrl: './add-contact-dialog.component.html',
  styleUrls: ['./add-contact-dialog.component.css']
})
export class AddContactDialogComponent {
  contactForm: any;
  constructor(
    public dialogRef: MatDialogRef<AddContactDialogComponent>,
    @Inject(MAT_DIALOG_DATA) public data: any,
    private formBuilder: FormBuilder
  ) {
    this.contactForm = this.formBuilder.group({
      firstName: ['', Validators.required],
      lastName: ['', Validators.required],
      phoneId: ['', Validators.required],
      categoryId: ['', Validators.required]
    });
  }

  onCancel(): void {
    this.dialogRef.close();
  }

  onSubmit(): void {
    console.log('onSubmit() called');
    if (this.contactForm.valid) {
      console.log('Form is valid');

     // const contactForm.value = this.contactForm.value.firstName;
      console.log('Добавлен новый контакт:', this.contactForm.value);

      // Закрываем диалоговое окно и передаем данные в родительский компонент
      this.dialogRef.close();
      console.log('Диалог успешно закрыт в onSubmit!');
    } else {
      console.log('Form is invalid');
    }
  }
}
<h2 mat-dialog-title>Добавление контакта</h2>
<div mat-dialog-content>
  <form [formGroup]="contactForm" (ngSubmit)="onSubmit()">
    <mat-form-field>
      <input matInput placeholder="Имя" formControlName="firstName" required>
      <mat-error *ngIf="contactForm.get('firstName').hasError('required')">Имя обязательно</mat-error>
    </mat-form-field>

    <mat-form-field>
      <input matInput placeholder="Фамилия" formControlName="lastName" required>
      <mat-error *ngIf="contactForm.get('lastName').hasError('required')">Фамилия обязательна</mat-error>
    </mat-form-field>

    <mat-form-field>
      <input matInput placeholder="Номер телефона" formControlName="phoneId" required>
      <mat-error *ngIf="contactForm.get('phoneId').hasError('required')">Номер телефона обязателен</mat-error>
    </mat-form-field>

    <mat-form-field>
      <input matInput placeholder="Категория" formControlName="categoryId" required>
      <mat-error *ngIf="contactForm.get('categoryId').hasError('required')">Категория обязательна</mat-error>
    </mat-form-field>
  </form>
</div>
<div mat-dialog-actions>
  <button  class="btn btn-primary" (click)="onCancel()">Отмена</button>
  <button  class="btn btn-primary" (click)="onSubmit()" [disabled]="!contactForm.valid">Добавить контакт</button>
</div>
